var mensaje=document.getElementById("mensaje");function contarPalabras(){this.value.trim().replace(/\s{2,}/g," ").split(" ").length>150?(this.validity.valid=!1,this.setCustomValidity("Este campo admite un m√°ximo de 150 palabras")):(this.validity.valid=!0,this.setCustomValidity(""))}mensaje.addEventListener("blur",contarPalabras);var formulario=document.getElementById("contactoForm");function enviar(e){grabarFormulario(),e.preventDefault()}formulario.addEventListener("submit",enviar);var seleccionable=document.getElementById("motivo");function grabarFormulario(){formulario.nombre.value,formulario.email.value;formulario.reset()}seleccionable.addEventListener("change",function(){"otro"==this.value?document.getElementById("inputOculto").classList.remove("oculto"):document.getElementById("inputOculto").classList.add("oculto")});